    private void botonAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAgregarActionPerformed
        ArchivoDeTexto archivo = new ArchivoDeTexto();                          //CLASE ARCHIVO
        archivo.crarArchivoDeContactos();                                       //CREA ARCHIVO
        String nombre = "", celular = "", correo = "";
        nombre = textFieldNombre.getText();                                     //GUARDA NOMBRE
        celular = textFieldCelular.getText();                                   //GUARDA CELULAR
        correo = textFieldCorreoElectronico.getText();                          //GUARDA CORREO
        Persona persona1 = new Persona(nombre, celular, correo);                //GUARDA DATOS PERSONA EN LA CLASE
        archivo.escribirTexto(persona1);                                        //ESCRIBIR DATOS PERSONA EN ARCHIVO
        //REINICIO DE LAS CAJAS DE TEXTO
        textFieldNombre.setText("");
        textFieldCelular.setText("");
        textFieldCorreoElectronico.setText("");
    }//GEN-LAST:event_botonAgregarActionPerformed
